mapper.select:
select
,
from member_point_record as mpr left join member as m on mpr.member_id = m.id where id = #{id,jdbcType=BIGINT}

select

,

from member_point_record as mpr
left join member as m
on mpr.member_id = m.id
where mpr.type = #{type,jdbcType=INTEGER}

	and mpr.point_pool_id = #{pointPoolId,jdbcType=BIGINT}

order by mpr.create_at desc
limit #{page.pageStart}, #{page.pageSize}
