mapper.select:
select

from point_pool
where start_at <= #{now,jdbcType=BIGINT}
 and end_at >= #{now,jdbcType=BIGINT}

mapper.update:

update point_pool
set name = #{name,jdbcType=VARCHAR},
start_at = #{startAt,jdbcType=INTEGER},
end_at = #{endAt,jdbcType=INTEGER},
total_point = #{totalPoint,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
